DROP TABLE if exists ffm_programs;

CREATE TABLE "ffm_programs" (
  id serial NOT NULL,
  program_code varchar,
  program_name varchar,
  help_url varchar,
  web_site_url varchar,
  signup_url varchar,
  alliance varchar,
  miles_type_configuration json,
  tiers_configuration json,
  airlines json,
  "createdAt" timestamp with time zone,
  "updatedAt" timestamp with time zone,
  CONSTRAINT ffm_programs_pkey PRIMARY KEY (id)
)
WITH (
  OIDS=FALSE
);

ALTER TABLE "ffm_programs"
  OWNER TO avaea;

CREATE INDEX ON ffm_programs ((lower(program_code)));

